import{_ as a,o,c as i,f as e,g as t}from"./app.12fd7768.js";const r="/image/ponte-cidade-jardim.png",d="/image/rio-pinheiros-antes-e-depois.jpg",s="/image/equation-solidos-totais.png",n="/image/chart-absorbancia.png",l="/image/chart-coliformes-fecais.png",c="/image/chart-ph.png",m="/image/chart-dbo.png",u="/image/chart-turbidez.png",p="/image/chart-nitrogenio-total.png",g="/image/chart-fosforo.png",h="/image/chart-oxigenio-dissolvido.png",f="/image/chart-residuos-totais.png",b="/image/chart-historico-coliformes.png",_="/image/chart-historico-ph.png",x="/image/chart-historico-dbo.png",v="/image/chart-historico-nitrogenio-total.png",q="/image/chart-historico-fosforo-total.png",T="/image/chart-historico-temperatura.png",y="/image/chart-historico-turbidez.png",z="/image/chart-historico-residuos-totais.png",A="/image/chart-historico-oxigenio.png",C="/image/chart-historico-iqa.png",H=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"1.\\tIntrodu\xE7\xE3o","slug":"_1-introducao","link":"#_1-introducao","children":[{"level":3,"title":"1.1.\\tA import\xE2ncia do estudo das \xE0guas","slug":"_1-1-a-importancia-do-estudo-das-aguas","link":"#_1-1-a-importancia-do-estudo-das-aguas","children":[]},{"level":3,"title":"1.2.\\tMapa da localiza\xE7\xE3o do estudo","slug":"_1-2-mapa-da-localizacao-do-estudo","link":"#_1-2-mapa-da-localizacao-do-estudo","children":[]},{"level":3,"title":"1.3.\\tCaracter\xEDsticas do Rio Pinheiros","slug":"_1-3-caracteristicas-do-rio-pinheiros","link":"#_1-3-caracteristicas-do-rio-pinheiros","children":[]}]},{"level":2,"title":"2.\\t\xCDndice de Qualidade da \xC0gua (IQA)","slug":"_2-indice-de-qualidade-da-agua-iqa","link":"#_2-indice-de-qualidade-da-agua-iqa","children":[{"level":3,"title":"2.1.\\tOxig\xEAnio dissolvido","slug":"_2-1-oxigenio-dissolvido","link":"#_2-1-oxigenio-dissolvido","children":[]},{"level":3,"title":"2.2.\\tPh","slug":"_2-2-ph","link":"#_2-2-ph","children":[]},{"level":3,"title":"2.3.\\tTemperatura","slug":"_2-3-temperatura","link":"#_2-3-temperatura","children":[]},{"level":3,"title":"2.4.\\tColiformes Tomotolerantes","slug":"_2-4-coliformes-tomotolerantes","link":"#_2-4-coliformes-tomotolerantes","children":[]},{"level":3,"title":"2.5.\\tDemanda Bioqu\xEDmica de Oxig\xEAnio (DBO5,20)","slug":"_2-5-demanda-bioquimica-de-oxigenio-dbo5-20","link":"#_2-5-demanda-bioquimica-de-oxigenio-dbo5-20","children":[]},{"level":3,"title":"2.6.\\tNitrog\xEAnio Total","slug":"_2-6-nitrogenio-total","link":"#_2-6-nitrogenio-total","children":[]},{"level":3,"title":"2.7.\\tF\xF3sforo Total","slug":"_2-7-fosforo-total","link":"#_2-7-fosforo-total","children":[]},{"level":3,"title":"2.8.\\tRes\xEDduo Total","slug":"_2-8-residuo-total","link":"#_2-8-residuo-total","children":[]},{"level":3,"title":"2.9.\\tTurbidez","slug":"_2-9-turbidez","link":"#_2-9-turbidez","children":[]}]},{"level":2,"title":"3.\\tLaborat\xF3rio","slug":"_3-laboratorio","link":"#_3-laboratorio","children":[{"level":3,"title":"3.1\\tM\xE9todo Nitrog\xEAnio Kjeldahl: Determina\xE7\xE3o de Am\xF4nia (NH 3) e Nitrog\xEAnio Amoniacal","slug":"_3-1metodo-nitrogenio-kjeldahl-determinacao-de-amonia-nh-3-e-nitrogenio-amoniacal","link":"#_3-1metodo-nitrogenio-kjeldahl-determinacao-de-amonia-nh-3-e-nitrogenio-amoniacal","children":[]},{"level":3,"title":"3.2.\\tDetermina\xE7\xE3o de S\xF3lidos Totais","slug":"_3-2-determinacao-de-solidos-totais","link":"#_3-2-determinacao-de-solidos-totais","children":[]},{"level":3,"title":"3.3.\\tDetermina\xE7\xE3o de Turbidez","slug":"_3-3-determinacao-de-turbidez","link":"#_3-3-determinacao-de-turbidez","children":[]},{"level":3,"title":"3.4.\\tOD e DBO","slug":"_3-4-od-e-dbo","link":"#_3-4-od-e-dbo","children":[]},{"level":3,"title":"3.5.\\tDetermina\xE7\xE3o de F\xF3sforo Totais","slug":"_3-5-determinacao-de-fosforo-totais","link":"#_3-5-determinacao-de-fosforo-totais","children":[]},{"level":3,"title":"3.6.\\tContagem de Col\xF4nias (Coliformes)","slug":"_3-6-contagem-de-colonias-coliformes","link":"#_3-6-contagem-de-colonias-coliformes","children":[]},{"level":3,"title":"3.7.\\tPH","slug":"_3-7-ph","link":"#_3-7-ph","children":[]}]},{"level":2,"title":"4. Resultados","slug":"_4-resultados","link":"#_4-resultados","children":[]},{"level":2,"title":"5.\\tContexto His\xF3torico","slug":"_5-contexto-hisotorico","link":"#_5-contexto-hisotorico","children":[{"level":3,"title":"5.1.\\tColiformes Fecais","slug":"_5-1-coliformes-fecais","link":"#_5-1-coliformes-fecais","children":[]},{"level":3,"title":"5.2.\\tPh","slug":"_5-2-ph","link":"#_5-2-ph","children":[]},{"level":3,"title":"5.2.\\tDBO","slug":"_5-2-dbo","link":"#_5-2-dbo","children":[]},{"level":3,"title":"5.2.\\tNitrog\xEAnio Total","slug":"_5-2-nitrogenio-total","link":"#_5-2-nitrogenio-total","children":[]},{"level":3,"title":"5.5.\\tF\xF3sforo Total","slug":"_5-5-fosforo-total","link":"#_5-5-fosforo-total","children":[]},{"level":3,"title":"5.6.\\tTemperatura","slug":"_5-6-temperatura","link":"#_5-6-temperatura","children":[]},{"level":3,"title":"5.7.\\tTurbidez","slug":"_5-7-turbidez","link":"#_5-7-turbidez","children":[]},{"level":3,"title":"5.8.\\tRes\xEDduo Total","slug":"_5-8-residuo-total","link":"#_5-8-residuo-total","children":[]},{"level":3,"title":"5.9.\\tOxig\xEAnio Dissolvido","slug":"_5-9-oxigenio-dissolvido","link":"#_5-9-oxigenio-dissolvido","children":[]}]},{"level":2,"title":"6. Conclus\xF5es","slug":"_6-conclusoes","link":"#_6-conclusoes","children":[]}],"relativePath":"reports/2022.md"}'),O={name:"reports/2022.md"},k=e('<h1 align="center"> Monitorando as \xC1guas:<br>Ponte Cidade Jardim 2022 </h1><p><img src="'+r+'" alt="Ponte Cidade Jardim"></p><p align="center"><b>Figura 1 - Vis\xE3o a\xE9rea da ponde Cidade Jardim.</b></p><nav class="table-of-contents"><ul><li><a href="#_1-introducao">1. Introdu\xE7\xE3o</a><ul><li><a href="#_1-1-a-importancia-do-estudo-das-aguas">1.1. A import\xE2ncia do estudo das \xE0guas</a></li><li><a href="#_1-2-mapa-da-localizacao-do-estudo">1.2. Mapa da localiza\xE7\xE3o do estudo</a></li><li><a href="#_1-3-caracteristicas-do-rio-pinheiros">1.3. Caracter\xEDsticas do Rio Pinheiros</a></li></ul></li><li><a href="#_2-indice-de-qualidade-da-agua-iqa">2. \xCDndice de Qualidade da \xC0gua (IQA)</a><ul><li><a href="#_2-1-oxigenio-dissolvido">2.1. Oxig\xEAnio dissolvido</a></li><li><a href="#_2-2-ph">2.2. Ph</a></li><li><a href="#_2-3-temperatura">2.3. Temperatura</a></li><li><a href="#_2-4-coliformes-tomotolerantes">2.4. Coliformes Tomotolerantes</a></li><li><a href="#_2-5-demanda-bioquimica-de-oxigenio-dbo5-20">2.5. Demanda Bioqu\xEDmica de Oxig\xEAnio (DBO5,20)</a></li><li><a href="#_2-6-nitrogenio-total">2.6. Nitrog\xEAnio Total</a></li><li><a href="#_2-7-fosforo-total">2.7. F\xF3sforo Total</a></li><li><a href="#_2-8-residuo-total">2.8. Res\xEDduo Total</a></li><li><a href="#_2-9-turbidez">2.9. Turbidez</a></li></ul></li><li><a href="#_3-laboratorio">3. Laborat\xF3rio</a><ul><li><a href="#_3-1metodo-nitrogenio-kjeldahl-determinacao-de-amonia-nh-3-e-nitrogenio-amoniacal">3.1 M\xE9todo Nitrog\xEAnio Kjeldahl: Determina\xE7\xE3o de Am\xF4nia (NH 3) e Nitrog\xEAnio Amoniacal</a></li><li><a href="#_3-2-determinacao-de-solidos-totais">3.2. Determina\xE7\xE3o de S\xF3lidos Totais</a></li><li><a href="#_3-3-determinacao-de-turbidez">3.3. Determina\xE7\xE3o de Turbidez</a></li><li><a href="#_3-4-od-e-dbo">3.4. OD e DBO</a></li><li><a href="#_3-5-determinacao-de-fosforo-totais">3.5. Determina\xE7\xE3o de F\xF3sforo Totais</a></li><li><a href="#_3-6-contagem-de-colonias-coliformes">3.6. Contagem de Col\xF4nias (Coliformes)</a></li><li><a href="#_3-7-ph">3.7. PH</a></li></ul></li><li><a href="#_4-resultados">4. Resultados</a></li><li><a href="#_5-contexto-hisotorico">5. Contexto His\xF3torico</a><ul><li><a href="#_5-1-coliformes-fecais">5.1. Coliformes Fecais</a></li><li><a href="#_5-2-ph">5.2. Ph</a></li><li><a href="#_5-2-dbo">5.2. DBO</a></li><li><a href="#_5-2-nitrogenio-total">5.2. Nitrog\xEAnio Total</a></li><li><a href="#_5-5-fosforo-total">5.5. F\xF3sforo Total</a></li><li><a href="#_5-6-temperatura">5.6. Temperatura</a></li><li><a href="#_5-7-turbidez">5.7. Turbidez</a></li><li><a href="#_5-8-residuo-total">5.8. Res\xEDduo Total</a></li><li><a href="#_5-9-oxigenio-dissolvido">5.9. Oxig\xEAnio Dissolvido</a></li></ul></li><li><a href="#_6-conclusoes">6. Conclus\xF5es</a></li></ul></nav><h2 id="_1-introducao" tabindex="-1">1. Introdu\xE7\xE3o <a class="header-anchor" href="#_1-introducao" aria-hidden="true">#</a></h2><h3 id="_1-1-a-importancia-do-estudo-das-aguas" tabindex="-1">1.1. A import\xE2ncia do estudo das \xE0guas <a class="header-anchor" href="#_1-1-a-importancia-do-estudo-das-aguas" aria-hidden="true">#</a></h3><p>O projeto atual tem como objetivo realizar uma an\xE1lise completa do estado das \xE1guas do Rio Pinheiros, mais especificamente das amostras coletadas logo abaixo da ponte cidade jardim. Neste estudo, ser\xE1 calculado o \xEDndice de qualidade de \xE1gua (IQA) e depois contrastar tais dados com a norma da CONAMA 357 para definir o n\xEDvel de qualidade das \xE1guas no ponto de coleta.</p><p>Primeiramente, \xE9 necess\xE1rio relembrar o porqu\xEA o estudo da \xE1gua \xE9 importante. A relev\xE2ncia da \xE1gua \xE9 ineg\xE1vel, j\xE1 que tal componente comp\xF5e 70% do peso corporal humano, regula a temperatura do corpo e \xE9 essencial para todas as fun\xE7\xF5es org\xE2nicas. Dessa forma, analisar a qualidade dos rios, buscando entender os impactos humanos que s\xE3o causados nesse ecossistema, \xE9 mais do que imprescind\xEDvel para o mantimento da qualidade da vida humana no planeta.</p><h3 id="_1-2-mapa-da-localizacao-do-estudo" tabindex="-1">1.2. Mapa da localiza\xE7\xE3o do estudo <a class="header-anchor" href="#_1-2-mapa-da-localizacao-do-estudo" aria-hidden="true">#</a></h3>',9),P=t("iframe",{src:"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3656.4897234526666!2d-46.69373308449337!3d-23.586763468417168!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x94ce5781fe4340c3%3A0xea53a09ff5b698af!2sPonte%20Cidade%20Jardim!5e0!3m2!1spt-BR!2sbr!4v1668488630169!5m2!1spt-BR!2sbr",width:"800",height:"600",style:{width:"100%",border:"0"},allowfullscreen:"",loading:"lazy",referrerpolicy:"no-referrer-when-downgrade"},null,-1),F=e('<p align="center"><b>Figura 2 - Mapa da regi\xE3o.</b><br><a href="https://goo.gl/maps/2fz5MCKs6J9aZcJe9">Google (2022)</a></p><h3 id="_1-3-caracteristicas-do-rio-pinheiros" tabindex="-1">1.3. Caracter\xEDsticas do Rio Pinheiros <a class="header-anchor" href="#_1-3-caracteristicas-do-rio-pinheiros" aria-hidden="true">#</a></h3><p>O Rio Pinheiros \xE9 um dos principais rios da cidade de S\xE3o Paulo, margeado pela marginal Pinheiros que junto com a marginal Tiet\xEA, comp\xF5em o principal sistema vi\xE1rio da cidade, onde 70% do fluxo total de ve\xEDculos passa por uma das marginais diariamente. O Rio nasce do encontro do Rio Guarapiranga com o Rio Grande, e des\xE1gua no Tiet\xEA.</p><p>Antigamente, o Rio Pinheiros era muito utilizado para o transporte, ocasionando em um grande povoamento de suas margens, principalmente no in\xEDcio do s\xE9culo XX por imigrantes italianos e japoneses. Devido a esse povoamento, as inunda\xE7\xF5es eram frequentes e buscando acabar com tal problema, em 1940, foram iniciadas as obras de retifica\xE7\xE3o do Rio Pinheiros, mesmo antes de suas \xE1guas estarem contaminadas pela polui\xE7\xE3o.</p><p><img src="'+d+'" alt="Antes e depois"></p><p align="center"><b>Figura 3 - Rio Pinheiros antes e depois.</b><br><a href="https://www.encontrapinheiros.com.br/pinheiros/rio-pinheiros/">Encontra Pinheiros (2022)</a></p><h2 id="_2-indice-de-qualidade-da-agua-iqa" tabindex="-1">2. \xCDndice de Qualidade da \xC0gua (IQA) <a class="header-anchor" href="#_2-indice-de-qualidade-da-agua-iqa" aria-hidden="true">#</a></h2><p>O \xEDndice de Qualidade das \xC1guas foi criado em 1970 e foi \u201Cdesenvolvido para avaliar a qualidade da \xE1gua bruta visando seu uso para o abastecimento p\xFAblico, ap\xF3s tratamento.\u201D[1]</p><p>O IQA analisa 9 par\xE2metros que possuem seus respectivos pesos associados de acordo com a sua import\xE2ncia de conformidade global da qualidade da \xE1gua. Sua avalia\xE7\xE3o, portanto, possui limita\xE7\xF5es j\xE1 que esse \xEDndice n\xE3o analisa muitos par\xE2metros tamb\xE9m importantes para averigua\xE7\xE3o da qualidade da \xE1gua para abastecimento p\xFAblico.</p><p>Vamos analisar e entender um pouco mais sobre a import\xE2ncia dos par\xE2metros abordados pelo IQA:</p><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>To do</p></div><h3 id="_2-1-oxigenio-dissolvido" tabindex="-1">2.1. Oxig\xEAnio dissolvido <a class="header-anchor" href="#_2-1-oxigenio-dissolvido" aria-hidden="true">#</a></h3><p>V\xE1rios organismos aqu\xE1ticos necessitam de oxig\xEAnio para a realiza\xE7\xE3o de sua atividade metab\xF3lica. Portanto, esse par\xE2metro \xE9 fundamental para a preserva\xE7\xE3o da vida aqu\xE1tica de um rio. \xC1guas mais polu\xEDdas e ricas em mat\xE9ria org\xE2nica costumam apresentar BAIXA concentra\xE7\xE3o de oxig\xEAnio dissolvido, visto que o mesmo \xE9 consumido para o processo de degrada\xE7\xE3o dessa mat\xE9ria. Por outro lado , \xE1guas mais limpas costumam apresentar concentra\xE7\xF5es de oxig\xEAnio dissolvido mais elevadas.</p><h3 id="_2-2-ph" tabindex="-1">2.2. Ph <a class="header-anchor" href="#_2-2-ph" aria-hidden="true">#</a></h3><p>Diversas esp\xE9cies aqu\xE1ticas possuem seu metabolismo afetado por diferentes n\xEDveis de pH. De acordo com o CONAMA o valor \xF3timo para a vida aqu\xE1tica est\xE1 entre 6 e 9.</p><h3 id="_2-3-temperatura" tabindex="-1">2.3. Temperatura <a class="header-anchor" href="#_2-3-temperatura" aria-hidden="true">#</a></h3><p>V\xE1rios par\xE2metros f\xEDsico-qu\xEDmicos da \xE1gua s\xE3o influenciados pela mudan\xE7a de temperatura, como exemplo a tens\xE3o superficial e a viscosidade. Caso atingidos por temperaturas fo a de seus limites de toler\xE2ncia, seres aqu\xE1ticos podem ter seu crescimento e taxa reprodutiva gravemente alterados.</p><h3 id="_2-4-coliformes-tomotolerantes" tabindex="-1">2.4. Coliformes Tomotolerantes <a class="header-anchor" href="#_2-4-coliformes-tomotolerantes" aria-hidden="true">#</a></h3><p>Indicativo de polui\xE7\xE3o de rios por esgoto dom\xE9stico. A presen\xE7a dessas bact\xE9rias em grande n\xFAmero pode indicar a exist\xEAncia de pat\xF3genos respons\xE1veis pela transmiss\xE3o de doen\xE7as de veicula\xE7\xE3o h\xEDdrica.</p><h3 id="_2-5-demanda-bioquimica-de-oxigenio-dbo5-20" tabindex="-1">2.5. Demanda Bioqu\xEDmica de Oxig\xEAnio (DBO5,20) <a class="header-anchor" href="#_2-5-demanda-bioquimica-de-oxigenio-dbo5-20" aria-hidden="true">#</a></h3><p>\xC9 a \u201Cquantidade de oxig\xEAnio necess\xE1ria para oxidar a mat\xE9ria org\xE2nica presente na \xE1gua atrav\xE9s da decomposi\xE7\xE3o microbiana aer\xF3bia\u201D. Altos n\xEDveis desse par\xE2metro indicam grande concentra\xE7\xE3o de mat\xE9ria org\xE2nica, principalmente relacionado ao despejo de esgotos dom\xE9sticos no rio, isso aliado com a diminui\xE7\xE3o dos valores de oxig\xEAnio dissolvido na \xE1gua, podem provocar mortandades de organismos aqu\xE1ticos da regi\xE3o.</p><h3 id="_2-6-nitrogenio-total" tabindex="-1">2.6. Nitrog\xEAnio Total <a class="header-anchor" href="#_2-6-nitrogenio-total" aria-hidden="true">#</a></h3><blockquote><p>To do</p></blockquote><h3 id="_2-7-fosforo-total" tabindex="-1">2.7. F\xF3sforo Total <a class="header-anchor" href="#_2-7-fosforo-total" aria-hidden="true">#</a></h3><p>O f\xF3sforo \xE9 um importante nutriente para os processos biol\xF3gicos e seu excesso pode causar a eutrofiza\xE7\xE3o das \xE1guas. Para conhecer mais sobre eutrofiza\xE7\xE3o, veja o item sobre o \xCDndice de Estado Tr\xF3fico.</p><p>Entre as fontes de f\xF3sforo destacam-se os esgotos dom\xE9sticos, pela presen\xE7a dos detergentes superfosfatados e da pr\xF3pria mat\xE9ria fecal. A drenagem pluvial de \xE1reas agr\xEDcolas e urbanas tamb\xE9m \xE9 uma fonte significativa de f\xF3sforo para os corpos d\u2019\xE1gua. Entre os efluentes industriais destacam-se os das ind\xFAstrias de fertilizantes, aliment\xEDcias, latic\xEDnios, frigor\xEDficos e abatedouros.</p><h3 id="_2-8-residuo-total" tabindex="-1">2.8. Res\xEDduo Total <a class="header-anchor" href="#_2-8-residuo-total" aria-hidden="true">#</a></h3><blockquote><p>\xC9 a mat\xE9ria que permanece ap\xF3s a evapora\xE7\xE3o, secagem ou calcina\xE7\xE3o da amostra de \xE1gua durante um determinado tempo e temperatura.</p></blockquote><p>Quando depositados em leitos dos rios podem causar seu assoreamento, gerando problemas de navega\xE7\xE3o e aumentando o risco de enchentes. Podem tamb\xE9m causar danos aos seres aqu\xE1ticos pois podem destruir locais de desova de peixes dentre outros danos.</p><h3 id="_2-9-turbidez" tabindex="-1">2.9. Turbidez <a class="header-anchor" href="#_2-9-turbidez" aria-hidden="true">#</a></h3><blockquote><p>Indica o grau de atenua\xE7\xE3o que um feixe de luz sofre ao atravessar a \xE1gua\u201D, tal efeito \xE9 causado pela absor\xE7\xE3o e espalhamento da luz sofrida por s\xF3lidos em suspens\xE3o. Atividades de minera\xE7\xE3o, despejo de esgotos dom\xE9stico ou industriais, causam uma eleva\xE7\xE3o da turbidez das \xE1guas.</p></blockquote><h2 id="_3-laboratorio" tabindex="-1">3. Laborat\xF3rio <a class="header-anchor" href="#_3-laboratorio" aria-hidden="true">#</a></h2><h3 id="_3-1metodo-nitrogenio-kjeldahl-determinacao-de-amonia-nh-3-e-nitrogenio-amoniacal" tabindex="-1">3.1 M\xE9todo Nitrog\xEAnio Kjeldahl: Determina\xE7\xE3o de Am\xF4nia (NH 3) e Nitrog\xEAnio Amoniacal <a class="header-anchor" href="#_3-1metodo-nitrogenio-kjeldahl-determinacao-de-amonia-nh-3-e-nitrogenio-amoniacal" aria-hidden="true">#</a></h3><p>Para a determina\xE7\xE3o da concentra\xE7\xE3o de nitrog\xEAnio total em nossa amostra, foi realizado o m\xE9todo Nitrog\xEAnio Kjeldahl. Foi utilizado esse m\xE9todo porque a partir dele conseguimos encontrar a concentra\xE7\xE3o total de nitrog\xEAnio em uma amostra com am\xF4nia e nitrog\xEAnio org\xE2nico, que s\xE3o as duas formas de nitrog\xEAnio mais encontradas em esgotos. O nitrog\xEAnio org\xE2nico est\xE1 contido em prote\xEDnas diversas enquanto o nitrog\xEAnio amoniacal \xE9 resultado da hidrolise da ureia na \xE1gua.</p><p>Neste m\xE9todo a amostra foi primeiramente digerida com \xE1cido sulf\xFArico e sulfato de merc\xFArio. Logo em seguida,</p><h3 id="_3-2-determinacao-de-solidos-totais" tabindex="-1">3.2. Determina\xE7\xE3o de S\xF3lidos Totais <a class="header-anchor" href="#_3-2-determinacao-de-solidos-totais" aria-hidden="true">#</a></h3><p>Primeiramente, um b\xE9quer contendo 250mL da amostra foi colocado em uma estufa por 10 minutos. Depois, foi adicionado mais 50mL da amostra ao b\xE9quer e com aux\xEDlio de uma chapa de aquecimento, o recipiente foi aquecido at\xE9 a evapora\xE7\xE3o total da amostra. Em seguida foi pesado o b\xE9quer seco ap\xF3s a evapora\xE7\xE3o da amostra (P1) e o b\xE9quer vazio (P2), em gramas. Com essas medidas foi feito o seguinte c\xE1lculo para encontrar o valor dos s\xF3lidos totais (ST):</p><p align="center"><img src="'+s+'" alt="Equa\xE7\xE3o para s\xF3lidos totais"></p><h3 id="_3-3-determinacao-de-turbidez" tabindex="-1">3.3. Determina\xE7\xE3o de Turbidez <a class="header-anchor" href="#_3-3-determinacao-de-turbidez" aria-hidden="true">#</a></h3><p>Para determinarmos a turbidez da amostra, foi utilizado um espectrofot\xF4metro de radia\xE7\xE3o ultravioleta para mensurar a absorb\xE2ncia da amostra ao ser exposta a uma radia\xE7\xE3o com comprimento de onda de 600 nm. Foi utilizado como refer\xEAncia a seguinte curva experimental:</p><p align="center"><img src="'+n+'" alt="Equa\xE7\xE3o para s\xF3lidos totais"><br><b>Figura 4 - Absorb\xE2ncia por turbudez.</b></p><h3 id="_3-4-od-e-dbo" tabindex="-1">3.4. OD e DBO <a class="header-anchor" href="#_3-4-od-e-dbo" aria-hidden="true">#</a></h3><blockquote><p>To do</p></blockquote><h3 id="_3-5-determinacao-de-fosforo-totais" tabindex="-1">3.5. Determina\xE7\xE3o de F\xF3sforo Totais <a class="header-anchor" href="#_3-5-determinacao-de-fosforo-totais" aria-hidden="true">#</a></h3><blockquote><p>To do</p></blockquote><h3 id="_3-6-contagem-de-colonias-coliformes" tabindex="-1">3.6. Contagem de Col\xF4nias (Coliformes) <a class="header-anchor" href="#_3-6-contagem-de-colonias-coliformes" aria-hidden="true">#</a></h3><blockquote><p>To do</p></blockquote><h3 id="_3-7-ph" tabindex="-1">3.7. PH <a class="header-anchor" href="#_3-7-ph" aria-hidden="true">#</a></h3><blockquote><p>To do</p></blockquote><h2 id="_4-resultados" tabindex="-1">4. Resultados <a class="header-anchor" href="#_4-resultados" aria-hidden="true">#</a></h2><p>Ap\xF3s todos os experimentos do laborat\xF3rio, \xE9 poss\xEDvel resumir todos os valores obtidos em uma figura s\xF3, conforme visto na tabela abaixo:</p><table><thead><tr><th style="text-align:left;">Resultado dos experimentos</th><th style="text-align:right;">Ponte Cidade Jardim (2022)</th></tr></thead><tbody><tr><td style="text-align:left;">Fosforo Total <code>mg/L</code></td><td style="text-align:right;"><code> 2,52</code></td></tr><tr><td style="text-align:left;">Ph <code>U.Ph</code></td><td style="text-align:right;"><code> 5,50</code></td></tr><tr><td style="text-align:left;">DBO <code>mg/L</code></td><td style="text-align:right;"><code> 90,00</code></td></tr><tr><td style="text-align:left;">Coliformes Fecais <code>UFC/100mL</code></td><td style="text-align:right;"><code> 4.400.000,00</code></td></tr><tr><td style="text-align:left;">Temperatura <code>\xB0C</code></td><td style="text-align:right;"><code> 5,00</code></td></tr><tr><td style="text-align:left;">Turbidez <code>UNT</code></td><td style="text-align:right;"><code> 34,07</code></td></tr><tr><td style="text-align:left;">Res\xEDduo Total <code>mg/L</code></td><td style="text-align:right;"><code> 520,00</code></td></tr><tr><td style="text-align:left;">Oxig\xEAnio dissolvido <code>%</code></td><td style="text-align:right;"><code> 11,50</code></td></tr></tbody></table><p><strong>Tabela 1 - Dados obtidos a partir dos experimentos feitos.</strong></p><p>A partir desses valores, pode-se achar o seu equivalente nos gr\xE1ficos de normas da CONAMA 357, de forma que seja poss\xEDvel descobrir o seu valor de qualidade. Para isso, pode-se digitalizar os gr\xE1ficos com as curvas m\xE9dias de varia\xE7\xE3o de qualidade da \xE1gua com os 9 par\xE2metros principais, de forma que seja poss\xEDvel achar cada ponto de qi das medidas feitas no laborat\xF3rio para enfim ser poss\xEDvel calcular o \xCDndice de Qualidade de \xC1gua (IQA) determinante do estado da \xE1gua. Portanto, os pontos est\xE3o presentes nos seguintes gr\xE1ficos:</p><p align="center"><img src="'+l+'" alt="Gr\xE1fico"><br><b>Figura 5 - Gr\xE1fico Coliformes Fecais.</b></p><br><p align="center"><img src="'+c+'" alt="Gr\xE1fico Ph"><br><b>Figura 6 - Gr\xE1fico Ph.</b></p><br><p align="center"><img src="'+m+'" alt="Gr\xE1fico DBO"><br><b>Figura 7 - Gr\xE1fico DBO.</b></p><br><p align="center"><img src="'+u+'" alt="Gr\xE1fico Turbidez"><br><b>Figura 8 - Gr\xE1fico Turbidez.</b></p><br><p align="center"><img src="'+p+'" alt="Gr\xE1fico Nitrog\xEAnio total"><br><b>Figura 9 - Gr\xE1fico Nitrog\xEAnio total.</b></p><br><p align="center"><img src="'+g+'" alt="Gr\xE1fico Fosforo"><br><b>Figura 10 - Gr\xE1fico Fosforo.</b></p><br><p align="center"><img src="'+h+'" alt="Gr\xE1fico Oxig\xEAnio dissolvido"><br><b>Figura 11 - Gr\xE1fico Oxig\xEAnio dissolvido.</b></p><br><p align="center"><img src="'+f+'" alt="Gr\xE1fico Res\xEDduos totais"><br><b>Figura 12 - Gr\xE1fico Res\xEDduos totais.</b></p><br><p>Tendo esses pontos, \xE9 poss\xEDvel perceber que todos grande maioria est\xE1 seguindo a curva de decl\xEDnio da qualidade da \xE1gua, tendo os coliformes fecais, DBO e res\xEDduos totais com qualidade extremamente baixa, enquanto outros est\xE3o na parte de in\xEDcio de decl\xEDnio da qualidade da \xE1gua ou na metade. Para observar ainda melhor, pode-se pegar cada qi da nossa amostra com seus devidos pesos e assim calcular o IQA da amostra, conforme pode ser visto na tabela seguinte:</p><table><thead><tr><th style="text-align:left;">Resultado experimentais</th><th style="text-align:right;">Ponte Cidade Jardim (2022)</th></tr></thead><tbody><tr><td style="text-align:left;">Coliformes Fecais <code>q1</code></td><td style="text-align:right;"><code> 3,00</code></td></tr><tr><td style="text-align:left;">Ph <code>q2</code></td><td style="text-align:right;"><code> 74,89</code></td></tr><tr><td style="text-align:left;">DBO <code>q3</code></td><td style="text-align:right;"><code> 2,00</code></td></tr><tr><td style="text-align:left;">Nitrog\xEAnio total <code>q4</code></td><td style="text-align:right;"><code> 79,08</code></td></tr><tr><td style="text-align:left;">Fosforo total <code>q5</code></td><td style="text-align:right;"><code> 16,53</code></td></tr><tr><td style="text-align:left;">Temperatura <code>q6</code></td><td style="text-align:right;"><code> 87,13</code></td></tr><tr><td style="text-align:left;">Turbidez <code>q7</code></td><td style="text-align:right;"><code> 49,67</code></td></tr><tr><td style="text-align:left;">Res\xEDduo Total <code>q8</code></td><td style="text-align:right;"><code> 32,00</code></td></tr><tr><td style="text-align:left;">Oxig\xEAnio dissolvido <code>q9</code></td><td style="text-align:right;"><code> 8,26</code></td></tr><tr><td style="text-align:left;"><strong>IQA</strong></td><td style="text-align:right;"><code> 17,55</code></td></tr></tbody></table><p><strong>Tabela 2 - C\xE1lculo do IQA a partir dos dados retirados do experimento e os gr\xE1ficos de normas.</strong></p><p>Conforme exemplificado na tabela de faixas de avalia\xE7\xE3o da qualidade da \xE1gua e, entre 0-19 \xE9 considerada uma \xE1gua de p\xE9ssima qualidade e inutiliz\xE1vel, o que n\xE3o \xE9 tanta surpresa visto os pontos nos gr\xE1ficos da CONAMA. Entretanto, para se obter um estudo ainda maior sobre a qualidade de \xE1gua da ponte Cidade Jardim, \xE9 poss\xEDvel criar gr\xE1ficos hist\xF3ricos que nos retornem a qualidade da \xE1gua desde 10 anos atr\xE1s, e junto com um contexto hist\xF3rico, ser poss\xEDvel entender porque a qualidade dessa \xE1gua est\xE1/continua t\xE3o ruim.</p><h2 id="_5-contexto-hisotorico" tabindex="-1">5. Contexto His\xF3torico <a class="header-anchor" href="#_5-contexto-hisotorico" aria-hidden="true">#</a></h2><p>Neste sentido, por meio dos dados disponibilizados da CETESB, foi pego o m\xEAs de novembro de 2012 a 2021 para observar como cada par\xE2metro se comportou e juntando o nosso experimento.</p><h3 id="_5-1-coliformes-fecais" tabindex="-1">5.1. Coliformes Fecais <a class="header-anchor" href="#_5-1-coliformes-fecais" aria-hidden="true">#</a></h3><p align="center"><img src="'+b+'" alt="Gr\xE1fico do hist\xF3rico de Coliformes Fecais"><br><b>Figura 13 - Gr\xE1fico do hist\xF3rico de Coliformes Fecais.</b></p><p>N\xE3o houve muita diferen\xE7a durante esses 10 anos, considerando o qi de qualidade, por\xE9m houve um aumento no ano de 2022.</p><h3 id="_5-2-ph" tabindex="-1">5.2. Ph <a class="header-anchor" href="#_5-2-ph" aria-hidden="true">#</a></h3><p align="center"><img src="'+_+'" alt="Gr\xE1fico do hist\xF3rico do Ph"><br><b>Figura 14 - Gr\xE1fico do hist\xF3rico do Ph.</b></p><p>O PH teve uma queda brusca nesse \xFAltimo ano em compara\xE7\xE3o com os anteriores.</p><h3 id="_5-2-dbo" tabindex="-1">5.2. DBO <a class="header-anchor" href="#_5-2-dbo" aria-hidden="true">#</a></h3><p align="center"><img src="'+x+'" alt="Gr\xE1fico do hist\xF3rico do DBO"><br><b>Figura 15 - Gr\xE1fico do hist\xF3rico do DBO.</b></p><p>O DBO diminuiu nos \xFAltimos dois anos, o que n\xE3o era esperado pela CONAMA.</p><h3 id="_5-2-nitrogenio-total" tabindex="-1">5.2. Nitrog\xEAnio Total <a class="header-anchor" href="#_5-2-nitrogenio-total" aria-hidden="true">#</a></h3><p align="center"><img src="'+v+'" alt="Gr\xE1fico do hist\xF3rico de Coliformes Fecais"><br><b>Figura 16 - Gr\xE1fico do hist\xF3rico de Coliformes Fecais.</b></p><p>Ocorreu tamb\xE9m um aumento no valor de nitrog\xEAnio, o que n\xE3o era esperado de forma alguma, tanto pelo decaimento que j\xE1 vinha acontecendo de 2020 como pela CONAMA.</p><h3 id="_5-5-fosforo-total" tabindex="-1">5.5. F\xF3sforo Total <a class="header-anchor" href="#_5-5-fosforo-total" aria-hidden="true">#</a></h3><p align="center"><img src="'+q+'" alt="Gr\xE1fico do hist\xF3rico de Fosforo total"><br><b>Figura 17 - Gr\xE1fico do hist\xF3rico de Fosforo total.</b></p><p>O valor do f\xF3sforo diminuiu para o \xFAltimo ano, o que faz sentido para o decaimento que vem acontecendo, por\xE9m n\xE3o para os valores da CONAMA.</p><h3 id="_5-6-temperatura" tabindex="-1">5.6. Temperatura <a class="header-anchor" href="#_5-6-temperatura" aria-hidden="true">#</a></h3><p align="center"><img src="'+T+'" alt="Gr\xE1fico do hist\xF3rico de Temperatura"><br><b>Figura 18 - Gr\xE1fico do hist\xF3rico de Temperatura.</b></p><p>A temperatura teve um aumento, por\xE9m foi bem pr\xF3ximo do que j\xE1 era esperado pela curva da CONAMA.</p><h3 id="_5-7-turbidez" tabindex="-1">5.7. Turbidez <a class="header-anchor" href="#_5-7-turbidez" aria-hidden="true">#</a></h3><p align="center"><img src="'+y+'" alt="Gr\xE1fico do hist\xF3rico de Turbudez"><br><b>Figura 19 - Gr\xE1fico do hist\xF3rico de Turbudez.</b></p><p>A turbidez diminuiu nos \xFAltimos anos, discordando da curva da CONAMA.</p><h3 id="_5-8-residuo-total" tabindex="-1">5.8. Res\xEDduo Total <a class="header-anchor" href="#_5-8-residuo-total" aria-hidden="true">#</a></h3><p align="center"><img src="'+z+'" alt="Gr\xE1fico do hist\xF3rico de Res\xEDduos totais"><br><b>Figura 20 - Gr\xE1fico do hist\xF3rico de Res\xEDduos totais.</b></p><p>Os s\xF3lidos diminu\xEDram tamb\xE9m, o que faz sentido considerando a diminui\xE7\xE3o da turbidez.</p><h3 id="_5-9-oxigenio-dissolvido" tabindex="-1">5.9. Oxig\xEAnio Dissolvido <a class="header-anchor" href="#_5-9-oxigenio-dissolvido" aria-hidden="true">#</a></h3><p align="center"><img src="'+A+'" alt="Gr\xE1fico do hist\xF3rico de Oxig\xEAnio dissolvido"><br><b>Figura 21 - Gr\xE1fico do hist\xF3rico de Oxig\xEAnio dissolvido.</b></p><p>O oxig\xEAnio dissolvido deu bem pr\xF3ximo do valor da CONAMA, e seguindo a curva crescente dos anos anteriores.</p><h2 id="_6-conclusoes" tabindex="-1">6. Conclus\xF5es <a class="header-anchor" href="#_6-conclusoes" aria-hidden="true">#</a></h2><p>Observando todos esses gr\xE1ficos, \xE9 poss\xEDvel perceber que o qi muda de acordo com o que \xE9 bom de cada par\xE2metro, por exemplo, como \xE9 melhor para a \xE1gua ter um valor de turbidez baixo, ent\xE3o seu qi vai ser mais alto, j\xE1 com o PH, como o ideal seria um PH igual a 7, caso o PH seja maior/menor que 7, ent\xE3o o qi vai abaixar para as duas situa\xE7\xF5es. Com isso, \xE9 poss\xEDvel achar os valores de IQA para cada ano e comparar com o valor obtido no experimento.</p><table><thead><tr><th style="text-align:left;">Data</th><th style="text-align:center;">IQA</th></tr></thead><tbody><tr><td style="text-align:left;">nov/2012</td><td style="text-align:center;"><code>21,09</code></td></tr><tr><td style="text-align:left;">nov/2013</td><td style="text-align:center;"><code>20,09</code></td></tr><tr><td style="text-align:left;">nov/2014</td><td style="text-align:center;"><code>15,70</code></td></tr><tr><td style="text-align:left;">nov/2015</td><td style="text-align:center;"><code>15,40</code></td></tr><tr><td style="text-align:left;">nov/2016</td><td style="text-align:center;"><code>21,71</code></td></tr><tr><td style="text-align:left;">nov/2017</td><td style="text-align:center;"><code>16,41</code></td></tr><tr><td style="text-align:left;">nov/2018</td><td style="text-align:center;"><code>20,58</code></td></tr><tr><td style="text-align:left;">nov/2019</td><td style="text-align:center;"><code>13,75</code></td></tr><tr><td style="text-align:left;">nov/2020</td><td style="text-align:center;"><code>20,14</code></td></tr><tr><td style="text-align:left;">nov/2021</td><td style="text-align:center;"><code>16,81</code></td></tr><tr><td style="text-align:left;"><strong>nov/2022</strong></td><td style="text-align:center;"><strong><code>17,85</code></strong></td></tr></tbody></table><p><strong>Tabela 3 - Hist\xF3rico do \xCDndice de Qualidade das \xC0guas.</strong></p><p align="center"><img src="'+C+'" alt="Gr\xE1fico do hist\xF3rico do \xCDndice de Qualidade das \xC0guas"><br><b>Figura 22 - Gr\xE1fico do hist\xF3rico do \xCDndice de Qualidade das \xC0guas.</b></p>',108),D=[k,P,F];function G(N,R,j,B,I,Q){return o(),i("div",null,D)}const S=a(O,[["render",G]]);export{H as __pageData,S as default};
